state={
    fullName:[]
}

handleSubmit = (e) => {
    e.preventDefault()
}
handleInputChange = (e) => {

this.setState({
    [e.target.name]: e.target.value
})
}

render() {
    const { fullName } = this.state
    return (
        <div>
            <h3>Forms and inputs</h3>
            <h5> fullname is : {fullName}</h5>

      <form onSubmit={this.handleSubmit}>     
      <input name="fullname" onChange={this.handleInputChange}/>
      </form>  
      <button>submit</button>

        </div>
    )
}
}

export default Form
